import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Empresa.css';

const EmpresaCard = ({ empresa }) => {
  const [mostrarDescripcionCompleta, setMostrarDescripcionCompleta] = useState(false);

  const toggleDescripcion = () => {
    setMostrarDescripcionCompleta(!mostrarDescripcionCompleta);
  };

  return (
    <div className="empresa-card">
      <div className="empresa-info">
        <img src={empresa.logoUrl} alt={`${empresa.nombre} logo`} className="empresa-logo" />
        <div className="empresa-details">
          <h2>{empresa.nombre}</h2>
          <p>{empresa.direccion}</p>
        </div>
      </div>
      <div className="empresa-extra-info">
        {empresa.caracteristicas.map((caracteristica, index) => (
          <div className="caracteristica" key={index}>
            <input type="checkbox" id={`caracteristica-${index}`} name={`caracteristica-${index}`} />
            <label htmlFor={`caracteristica-${index}`}>{caracteristica}</label>
          </div>
        ))}
      </div>
      <p className={`empresa-description ${mostrarDescripcionCompleta ? 'show-more' : ''}`}>
        {empresa.descripcion}
      </p>
      <a href="#" className="show-more-link" onClick={toggleDescripcion}>
        {mostrarDescripcionCompleta ? 'Mostrar menos' : 'Mostrar más'}
      </a>
      <p>
        <i className="fas fa-map-marker-alt"></i>
        <a href={empresa.mapaUrl} target="_blank" rel="noopener noreferrer">Ver en Google Maps</a>
      </p>
      <p>
        <i className="fas fa-external-link-alt"></i>
        <a href={empresa.webUrl} target="_blank" rel="noopener noreferrer">Página web</a>
      </p>
      <Link to={empresa.detallesUrl}>
        <button className="detalles-button">Ver detalles</button>
      </Link>
    </div>
  );
};

export default EmpresaCard;
