import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Perfil.css';

function Perfil() {
  const [nombre, setNombre] = useState('');
  const [usuario, setUsuario] = useState('');
  const [correo, setCorreo] = useState('');
  const [foto, setFoto] = useState('/ruta/imagen/default.jpg');
  const [isEditing, setIsEditing] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Cargar datos del perfil desde localStorage
    const storedNombre = localStorage.getItem('nombre');
    const storedUsuario = localStorage.getItem('usuario');
    const storedCorreo = localStorage.getItem('correo');
    const storedFoto = localStorage.getItem('foto');

    if (storedNombre) setNombre(storedNombre);
    if (storedUsuario) setUsuario(storedUsuario);
    if (storedCorreo) setCorreo(storedCorreo);
    if (storedFoto) setFoto(storedFoto);
  }, []);

  const handlePhotoUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setFoto(reader.result);
      localStorage.setItem('foto', reader.result); // Guardar en localStorage
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleSaveClick = () => {
    // Guardar datos actualizados en localStorage
    localStorage.setItem('nombre', nombre);
    localStorage.setItem('usuario', usuario);
    localStorage.setItem('correo', correo);
    setIsEditing(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'nombre') {
      setNombre(value);
    } else if (name === 'usuario') {
      setUsuario(value);
    } else if (name === 'correo') {
      setCorreo(value);
    }
  };

  return (
    <div className="perfil-container">
      <h1 className="perfil-header">Bienvenido {nombre.split(' ')[0]}</h1>
      <div className="perfil-content">
        <img
          src={foto}
          alt="Foto de perfil"
          className="perfil-foto"
        />
        <div className="perfil-info">
          <p>Usuario: {usuario}</p>
          <input type="file" accept="image/*" onChange={handlePhotoUpload} />
        </div>
      </div>
      <div className="perfil-enlaces">
        <a href="/datos" className="perfil-enlace">Mis datos</a>
        <p>
          Nombre: 
          {isEditing ? (
            <input
              type="text"
              name="nombre"
              value={nombre}
              onChange={handleInputChange}
            />
          ) : (
            <span>{nombre}</span>
          )}
          {isEditing ? (
            <button onClick={handleSaveClick}>Guardar</button>
          ) : (
            <button onClick={handleEditClick} className="perfil-enlace-button">Modificar</button>
          )}
        </p>
        <p>
          Usuario: 
          {isEditing ? (
            <input
              type="text"
              name="usuario"
              value={usuario}
              onChange={handleInputChange}
            />
          ) : (
            <span>{usuario}</span>
          )}
          {isEditing ? (
            <button onClick={handleSaveClick}>Guardar</button>
          ) : (
            <button onClick={handleEditClick} className="perfil-enlace-button">Modificar</button>
          )}
        </p>
        <p>
          Correo: 
          {isEditing ? (
            <input
              type="email"
              name="correo"
              value={correo}
              onChange={handleInputChange}
            />
          ) : (
            <span>{correo}</span>
          )}
          {isEditing ? (
            <button onClick={handleSaveClick}>Guardar</button>
          ) : (
            <button onClick={handleEditClick} className="perfil-enlace-button">Modificar</button>
          )}
        </p>
      </div>
      <div className="perfil-boton-container">
        <h2>¡Quiero hacer un envío!</h2>
        <button
          onClick={handleButtonClick}
          className="perfil-boton"
        >
          INGRESA AQUÍ
        </button>
      </div>
    </div>
  );
}

export default Perfil;
