import React, { useState } from 'react';
import './ListaAgencias.css';

const ConocenosMas = () => {
  const [rating, setRating] = useState(0);
  const [hover, setHover] = useState(0);
  const [comment, setComment] = useState("");

  const handleRating = (rate) => {
    setRating(rate);
  };

  const handleSubmit = () => {
    if (comment.trim()) {
      alert(`Rating: ${rating}\nComment: ${comment}`);
      setComment("");
    } else {
      alert("Porfavor deja tu comentario");
    }
  };

  return (
    <div className="conocenos-mas">
      <h1>CONÓCENOS MÁS</h1>
      <div className="company-info">
        <img src="/images/olva_courier.jpg" alt="Fachada de la empresa Olva Courier" className="company-photo" />
        <div className="company-details">
          <h2>Olva Courier</h2>
          <p>En OLVA, nos esforzamos por ofrecer un servicio de calidad que supere las expectativas de nuestros clientes. Trabajamos con actitudes positivas y vivimos nuestros valores en nuestro día a día.</p>
          <p>Olva Courier es una empresa peruana de logística y mensajería que ofrece una amplia gama de servicios para el envío de paquetes y documentos...</p>
          <p>La sede principal de Olva Courier se encuentra en:</p>
          <p>Avenida Argentina 4458, Callao 07001, Perú</p>
          <p>Horario de Entrega de Envíos:</p>
          <ul>
            <li>Lunes a Viernes: 8:00 AM a 5:00 PM</li>
            <li>Sábados: 8:00 AM a 3:00 PM</li>
          </ul>
          <p>Sitio Web: <a href="https://www.olvacourier.com/" target="_blank" rel="noopener noreferrer">https://www.olvacourier.com/</a></p>
        </div>
      </div>
      <div className="agencies">
        <h2>Conoce algunas de nuestras Agencias</h2>
        <ul>
          <li>OLVA COURIER - Cercado de Lima: Avenida Garcilazo de la Vega 1358 Cercado de Lima. Horario de atención: 9:30am a 7pm - lunes a sábado.</li>
          <li>OLVA COURIER - Rimac: Las Tapadas 198, Rimac, Lima. Horario de atención: 9:30am a 7:30pm - lunes a sábado.</li>
          <li>OLVA COURIER - La Victoria: Jirón Antonio Bazo 1280, La Victoria, Lima. Horario de atención: 8:00am a 6:30pm - lunes a sábado.</li>
        </ul>
      </div>
      <div className="comments-section">
        <h2>Comentarios</h2>
        <p>Deja tu comentarios (solo si estás logueado)</p>
        <div className="rating">
          {[...Array(5)].map((star, index) => {
            index += 1;
            return (
              <button
                type="button"
                key={index}
                className={index <= (hover || rating) ? "on" : "off"}
                onClick={() => handleRating(index)}
                onMouseEnter={() => setHover(index)}
                onMouseLeave={() => setHover(rating)}
              >
                <span className="star">&#9733;</span>
              </button>
            );
          })}
        </div>
        <textarea
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          placeholder="Deja tu comentario aquí"
          className="comment-box"
        />
        <button onClick={handleSubmit} className="submit-button">Enviar</button>
      </div>
    </div>
  );
};

export default ConocenosMas;
