import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Importa useHistory
import './Header.css';
import logo from '../assets/logo.jpeg';

const Header = ({ userName }) => {
  const [showDropdown, setShowDropdown] = useState(false);

  const history = useNavigate(); // Define la constante history

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const handleViewProfile = () => {
    history.push('/perfil'); // Redirige a la página de perfil
  };

  const handleLogout = () => {
    // Lógica para cerrar sesión
    // Aquí podrías limpiar el estado de autenticación y luego redirigir
    history.push('/empresas'); // Redirige a la página de inicio de sesión
  };

  return (
    <header className='header'>
      <div className='logo-slogan'>
        <img src={logo} alt='Envía Ya logo' className='logo' />
        <h1>Envía Ya</h1>
      </div>
      <nav>
        <ul className='nav-links'>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/destinos">Destinos</Link></li>
          <li><Link to="/empresas">Empresas</Link></li>
          <li><Link to="/nosotros">Nosotros</Link></li>
          <li><Link to="/Inicio">Inicio de Sesión</Link></li>
        </ul>
      </nav>
      {userName && (
        <div className="user-section" onClick={toggleDropdown}>
          Hola, {userName}
          {showDropdown && (
            <div className="user-dropdown">
              <div className="dropdown-item" onClick={handleViewProfile}>Ver Perfil</div>
              <div className="dropdown-item" onClick={handleLogout}>Cerrar Sesión</div>
            </div>
          )}
        </div>
      )}
    </header>
  );
};

export default Header;
